import React, { createContext,useReducer } from 'react'
import AppReducer from './AppReducer';

//  creating initial state
const initialState = {
    transactions: [
        {id:1, description:'project 1 income', transactionAmount:18000},
        {id:2, description:'project 2 income', transactionAmount:180090},
        {id:3, description:'project 1 expense', transactionAmount:-1800},
        {id:4, description:'project 2 expense', transactionAmount:-1890}
    ]
}
//  creting the global context
export const GlobalContext = createContext(initialState);

//  creting a provider for globalcontext
export const GlobalProvider = ({Children}) => {
    const [state,dispatch] = useReducer(AppReducer,initialState);
    return(
        <GlobalContext.Provider value={
            {
                transactions: state.transactions
            }
        }>
            {Children}
        </GlobalContext.Provider>

    );
}
